mapper.select:
select img_url
from item
where id = #{id,jdbcType=INTEGER}

select count(*)
from item
where sort = #{sort,jdbcType=INTEGER}

select

from item
order by update_time DESC limit #{amount,jdbcType=INTEGER}

select count(*)

from item
where store_name = #{storeName,jdbcType=VARCHAR}

select count(*)

from item
where title like #{keyWord,jdbcType=VARCHAR} or description like #{keyWord,jdbcType=VARCHAR}

mapper.update:
update item
set sales = sales + #{amount,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

update item
set sales = sales - #{amount,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER} and sales >= #{amount,jdbcType=INTEGER}
